import {configureStore, createAction, createSlice} from '@reduxjs/toolkit';

import { songsReducer } from './slices/songSlice';
import { moviesReducer } from './slices/movieSlice';







const store = configureStore({
    reducer:{
        songs:songsReducer,
        movies:moviesReducer
    }
});

// console.log(store)

export {store};
